import styled from 'styled-components'


export const RecipeImage = styled.img`
    width: 68vw;
    max-width: 600px;
    min-width: 300px;
    border-radius: 10px;
    margin-bottom: 20px;


`

export const ScreenContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 30px;



`

export const RecipeContainer = styled.div`
    display: flex;
    flex-direction: column;
    width: 60%;
    max-width: 600px;
    min-width: 300px;
`
